//Create App.js with class component
import React from 'react'
import Cards from './componenets/Cards/card'
import Chart from './componenets/Chart/Chart'
import CountryPicker from './componenets/CountryPicker/CountryPicker'
import styles from './App.module.css'
import { fetchData } from './api/index'
class App extends React.Component {
    //Data Ko Cards component ma use krny k liye usko as prop pass krrha hn
    state ={
        data : {},
        country: '',
    }
    async componentDidMount(){
        const fetchedData = await fetchData();
        this.setState({ data : fetchedData });
    }

    handleCountryChange = async (country) =>{
        const fetchedData = await fetchData(country);

        console.log(fetchData);
    }

    render(){
        const { data } = this.state;
        return(
            <div className={styles.container}>
                <Cards data={data} />
                <Chart />
                <CountryPicker handleCountryChange={this.componentWillUnmount(this.handleCountryChange)} />
            </div>
        )
    }
}
export default App;
